from django.conf.urls import urlfrom django.contrib.staticfiles.urls import staticfiles_urlpatternsfrom django.views.generic import RedirectViewfrom . import viewsurlpatterns = [    # url(r'^$', views.index, name='index'),    url(r'^cost_of_living/$', views.cost_of_living, name='cost_of_living'),    # url(r'^map/$', views.map, name='map'),]urlpatterns += staticfiles_urlpatterns()urlpatterns += [    url(r'^$', RedirectView.as_view(url='/cost_of_living/', permanent=True)),]